"#version 330 core
in vec2 fragTexCoord;
in vec4 fragColor;
out vec4 finalColor;

uniform sampler2D texture0;
uniform float progress;

void main()
{
    vec4 color = texture(texture0, fragTexCoord);

    // Create checkerboard pattern
    vec2 checker = floor(fragTexCoord * 8.0);
    float checkerPattern = mod(checker.x + checker.y, 2.0);

    // Different timing for each checker square
    float timing = checkerPattern * 0.5 + 0.5;
    float alpha = step(progress, timing);

    finalColor = vec4(color.rgb, color.a * alpha);
}"

"#version 330 core
in vec2 fragTexCoord;
in vec4 fragColor;
out vec4 finalColor;

uniform sampler2D texture0;
uniform sampler2D texture1;
uniform float progress;

void main()
{
    vec4 color1 = texture(texture0, fragTexCoord);
    vec4 color2 = texture(texture1, fragTexCoord);
    finalColor = mix(color1, color2, progress);
}"

"#version 330 core
in vec2 fragTexCoord;
in vec4 fragColor;
out vec4 finalColor;

uniform sampler2D texture0;
uniform float progress;

float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

void main()
{
    vec4 color = texture(texture0, fragTexCoord);
    float noise = random(fragTexCoord);
    float alpha = step(progress, noise);
    finalColor = vec4(color.rgb, color.a * alpha);
}"

"#version 330 core
in vec2 fragTexCoord;
in vec4 fragColor;
out vec4 finalColor;

uniform sampler2D texture0;
uniform float progress;

void main()
{
    vec4 color = texture(texture0, fragTexCoord);
    finalColor = vec4(color.rgb, color.a * (1.0 - progress));
}"

"#version 330 core
in vec2 fragTexCoord;
in vec4 fragColor;
out vec4 finalColor;

uniform sampler2D texture0;
uniform float progress;

float dot2(vec2 v) { return dot(v,v); }

float heart(vec2 p) {
    p.x = abs(p.x);
    if(p.y + p.x > 1.0)
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

void main()
{
    vec4 color = texture(texture0, fragTexCoord);
    vec2 center = (fragTexCoord - vec2(0.5, 0.5)) * 2.0;
    center.y = -center.y; // Flip Y for proper heart orientation
    float heartDist = heart(center);
    float threshold = (1.0 - progress) * 1.5 - 0.75; // Inverted progress for fade in
    float alpha = 1.0 - step(heartDist, threshold);
    finalColor = vec4(color.rgb, color.a * alpha);
}"

"#version 330 core
in vec2 fragTexCoord;
in vec4 fragColor;
out vec4 finalColor;

uniform sampler2D texture0;
uniform float progress;

void main()
{
    vec4 color = texture(texture0, fragTexCoord);
    vec2 center = vec2(0.5, 0.5);
    float dist = distance(fragTexCoord, center);
    float radius = 0.7 * (1.0 - progress);
    float alpha = step(dist, radius);
    finalColor = vec4(color.rgb, color.a * alpha);
}"

"#version 330 core
in vec2 fragTexCoord;
in vec4 fragColor;
out vec4 finalColor;

uniform sampler2D texture0;
uniform float progress;

void main()
{
    float pixelSize = 1.0 + progress * 50.0;
    vec2 pixelCoord = floor(fragTexCoord * pixelSize) / pixelSize;
    vec4 color = texture(texture0, pixelCoord);
    float alpha = 1.0 - progress;
    finalColor = vec4(color.rgb, color.a * alpha);
}"

