"#version 330 core
in vec2 fragTexCoord;
in vec4 fragColor;
out vec4 finalColor;

uniform sampler2D texture0;
uniform float progress;
uniform vec2 direction;

void main()
{
    vec2 offset = direction * progress;
    vec2 newCoord = fragTexCoord + offset;

    if (newCoord.x < 0.0 || newCoord.x > 1.0 || newCoord.y < 0.0 || newCoord.y > 1.0) {
        finalColor = vec4(0.0, 0.0, 0.0, 1.0);
    } else {
        vec4 color = texture(texture0, newCoord);
        finalColor = color;
    }
}"

"#version 330 core
in vec2 fragTexCoord;
in vec4 fragColor;
out vec4 finalColor;

uniform sampler2D texture0;
uniform float progress;

float star(vec2 p, float r, float n) {
    float an = 3.141593/n;
    float en = 3.141593/n;
    vec2 acs = vec2(cos(an),sin(an));
    vec2 ecs = vec2(cos(en),sin(en));
    float bn = mod(atan(p.x,p.y),2.0*an) - an;
    p = length(p)*vec2(cos(bn),abs(sin(bn)));
    p -= r*acs;
    p += ecs*clamp(-dot(p,ecs), 0.0, r*acs.y/ecs.y);
    return length(p)*sign(p.x);
}

void main()
{
    vec4 color = texture(texture0, fragTexCoord);
    vec2 center = fragTexCoord - vec2(0.5, 0.5);
    float starDist = star(center, 0.3, 5.0);
    float threshold = (progress - 0.5) * 0.8;
    float alpha = step(threshold, starDist);
    finalColor = vec4(color.rgb, color.a * alpha);
}"

